import Express from 'express';
import db from '../middleware/postgresAPI';
import FormValidator from '../middleware/FormValidator'
import multer from 'multer'
import fs from 'fs-extra'

const router = Express.Router();

const requestFormValidation = (preValidation, callback) => {
    return (req, res, next) => {
        if (req.body && req.body.data)
        req.body = JSON.parse(req.body.data);
        const formValidation = preValidation(req.body, req.method);
        if (formValidation.isValid)
        return callback(req, res, next);

        res.status(400).json(formValidation);
    };
};

const requestUpload = (preValidation, callback, { fileSize }) => {
  return (req, res, next) => {
    upload(fileSize).any()(req, res, err => {
      if (req.body && req.body.data && Array.isArray((req.body.data))) { 
        req.body = req.body.data.map(JSON.parse);
        req.body.map(row => {
          if (!empty(row.file))
            row.fileData = req.files.shift();
        });
      }

      if (req.body && req.body.data)
        req.body = JSON.parse(req.body.data);

      const formValidation = preValidation(req.body, err);
      if (formValidation.isValid){
        return callback(req, res, next);
      }
        

      res.status(400).json(formValidation);
      const pathList = req.files.map(row => (row.path));
      return deleteFiles(pathList);
    });
  };
};

router.get('/userInfo', (req, res) => { // cambiar los parametros con respecto al proyecto
    res.status(200).json({
      name: req.user.name,
      rut: req.user['user_id'],
      type: req.user['user_type_id']
    });
});


//GET

//POST

//PUT

//DELETE

router.all('*', (req, res) => {
    res.status(404).json({ message: 'La ruta de la solicitud HTTP no es reconocida por el servidor.' });
});

export default router;

