import connection from './postgresConnection';
import Rut from 'rutjs';
import User from '../models/user';
import empty from 'is-empty';

const functionQueries = {};

/**
 * Función que sirve para generar el menú lateral dado el tipo de usuario, por ejemplo si
 * el usuario que se intenta logear es de tipo Administrador, se muestra todo el menú pero si es por ejemplo
 * una secretaria, el menú solo contiene ciertos apartados.
 * También sirve para ver si el usuario está bloqueado o no.
 */

functionQueries.getAuthenticatedUserData = (req, res, next) => {
    let user = {
        id: req.user.id,
        userId: req.user['user_id'],
        name: req.user.name,
        type: req.user['user_type_id'],
        state: req.user.state
    };
    req.body.pathname = req.body.pathname.substring(1, req.body.pathname.length);
    let data = { type: user.type, pathname: req.body.pathname || '' };

    connection.tx(t => {
        let queries = [];
        // Consulta que toma la ruta actual y le pregunta a la base de datos si este usuario tiene permiso para entrar a ese link
        //en especifico
        let query = 'SELECT COUNT(*)\
                  FROM user_permission INNER JOIN system_page ON user_permission.system_page_id = system_page.id\
                  WHERE user_permission.user_type_id = ${type} AND system_page.link = ${pathname}';
        queries.push(t.one(query, data));
        //La consulta que devuelve todo el menu lateral dado el tipo de usuario , la respuesta que se espera es como la siguiente:
        // Menu Grande -> json{Sub-Menu1, Sub-Menu2, .... , Sub-Menu n}
        query = "SELECT menu_group.iconclass, menu_group.id, menu_group.menu, CASE WHEN menu_group.menu_order IS NULL THEN 0 ELSE menu_group.menu_order END AS menu_order,\
                json_agg(json_build_object('titulo', system_page.text, 'link', system_page.link, 'icon', system_page.icon) ORDER BY user_permission.menu_order) AS link\
              FROM user_permission\
                INNER JOIN system_page ON user_permission.system_page_id = system_page.id\
                LEFT JOIN menu_group ON user_permission.menu_group_id = menu_group.id\
              WHERE user_type_id = ${type} AND in_menu AND system_page.id != 14\
              GROUP BY menu_group.menu_order, menu_group.menu, menu_group.id, menu_group.iconclass\
              ORDER BY menu_order";

        queries.push(t.any(query, data));
        // Ejecuto las dos consultas simultaneamente con batch
        return t.batch(queries);
    })
        //Si el menu lateral no tiene submenu solamente agrego el titulo, pero si tiene sub-menus agrego cada sub-menu a ese menú
        //en particular
        .then(result => {
            let menu = [];
            result[1].map(row => {
                if (!row.menu)
                    row.link.map(menuItem => menu.push(menuItem));
                else
                    menu.push(row);
            });

            //let permission = ['/login','/', '*'].indexOf(data.pathname) != -1 || result[0].count > 0;
            res.status(user.state ? 200 : 403).json({
                logged: true,
                hasPermission: user.state,
                user,
                menu
            });
        })
        .catch(err => {
            console.log(err);
            res.status(500).json({ error: err, message: 'Error al obtener los datos del usuario.' });
        });
};

export default functionQueries;

