import Express from 'express';
import Passport from 'passport';
import User from '../models/user';
import db from '../middleware/postgresAPI';

const router = new Express.Router();

router.post('/login', Passport.authenticate('local'), (req, res) => {
  return res.json({
    success: true,
    message: 'Ha logrado ingresar al sistema con éxito!',
    user: req.user
  });
});

router.get('/checkLogin', (req, res) => {
  res.status(200).json({
    logged: true
  });
});

router.get('/logout', (req, res) => {
  req.logOut();
  res.redirect('/login');
});

router.get('/update', (req, res) => {
  User.update(
    '19.207.278-6','Eduardyto68',
    (err, user) => {
      if(err) {
        return res.status(400).json({
          success: false,
          message: 'Error al actualizar la contraseña.',
          error: err
        })
      } else {
        return res.status(200).json({
          success: true,
          message: 'La contraseña fue actualizada'
        })
      }
    }
  );
});


router.get('/register', (req, res) => {
  User.register(
    {user_id: '19.422.959-3', user_type_id: 'ADM', name: 'Francisca Hernández'},
    'proyectopenuelas18',
    (err, user) => 
    {
      console.log(err);
      res.json({user, err})
    }
  );
});


/* router.get('/register', (req, res) => {
  User.register(
    {user_id: '19.153.962-1', user_type_id: 'AD', name: 'Rosita Hormann'},
    'asd123',
    (err, user) => 
    {
      console.log(err);
      res.json({user, err})
    }
  );
}); */



export default router;

